<div class="row">    <div class="col-xs-8 col-md-3 pr-0">        <input class="form-control hidden" name="attributes[{{$key}}][display_name]" value="{{$slug}}">        <input class="form-control" value="{{$slug}}" disabled>    </div>    <div class="col-xs-4 col-md-2 pr-0">        <select class="form-control changeType"  name="attributes[{{$key}}][type]">            @if(!empty($type['name']))                <option value="{{$type['name']}}">{{$type['name']}}</option>            @else                <option value="text">Text</option>            @endif        </select>    </div>    <div class="col-xs-12 col-md-7">        @if(!empty($type['name']))            @switch($type['name'])                @case ('text')                    <textarea rows="2" name="attributes[{{$key}}][content]" id="{{$type['id']}}" maxlength="{{$type['max']}}" minlength="{{$type['min']}}" class="form-control {{$type['class']}}" placeholder="Input value" style="resize: none"  {{$type['required']}}></textarea>                    @break                @case ('richTextEditor')                    <textarea rows="2" name="attributes[{{$key}}][content]" id="{{$type['id']}}" class="form-control contentEditor {{$type['class']}}" placeholder="Input value" {{$type['required']}}></textarea>                    @break                @case ('number')                    <input type="number" name="attributes[{{$key}}][content]" id="{{$type['id']}}" max="{{$type['max']}}" min="{{$type['min']}}"  class="form-control {{$type['class']}}" {{$type['pattern']}} {{$type['required']}}>                    @break                @case ('email')                    <input type="email" name="attributes[{{$key}}][content]" id="{{$type['id']}}" class="form-control {{$type['class']}}" {{$type['pattern']}} {{$type['required']}}>                    @break                @case ('time')                    <input type="time" name="attributes[{{$key}}][content]" id="{{$type['id']}}" class="form-control {{$type['class']}}" {{$type['pattern']}} {{$type['required']}}>                    @break                @case ('boolean')                    <select class="form-control {{$type['class']}}" id="{{$type['id']}}"  name="attributes[{{$key}}][content]" {{$type['pattern']}} {{$type['required']}}>                        <option value="yes">Yes</option>                        <option value="no">No</option>                    </select>                    @break                @case ('image')                    <div class="form-group" {{$type['class']}} id="{{$type['id']}}">                        <div class=" media-loader-parent ">                            <div class="input-group">                                <span class="input-group-addon" ><i class="fa fa-upload"></i></span>                                <input name="attributes[{{$key}}][content]" autocomplete="off" type="text" class="form-control media-loader" placeholder="Choose file" {{$type['pattern']}} {{$type['required']}}>                            </div>                        </div>                    </div>                    @break                @case ('dates')                    <div class="form-group {{$type['class']}}" id="{{$type['id']}}">                        <div class="input-group">                            <div class="input-group-addon">                                <i class="fa fa-calendar"></i>                            </div>                            <input type="text" name="attributes[{{$key}}][content]" class="form-control pull-right datepicker" {{$type['pattern']}} {{$type['required']}}>                        </div>                    </div>                    @break                @case ('dateRanger')                    <div class="form-group {{$type['class']}}" id="{{$type['id']}}">                        <div class="input-group">                            <div class="input-group-addon">                                <i class="fa fa-calendar"></i>                            </div>                            <input type="text" name="attributes[{{$key}}][content]" class="form-control pull-right reservation" {{$type['pattern']}} {{$type['required']}}>                        </div>                    </div>                    @break                @case ('color')                    <div class="form-group {{$type['class']}}" id="{{$type['id']}}">                        <div class="input-group my-colorpicker colorpicker-element">                            <input type="text" name="attributes[{{$key}}][content]" class="form-control" {{$type['pattern']}} {{$type['required']}}>                            <div class="input-group-addon">                                <i></i>                            </div>                        </div>                    </div>                    @break                @default                    <textarea type="text" class="form-control" name="attributes[{{$key}}][content]" {{$type['required']}}></textarea>            @endswitch        @else            <textarea type="text" class="form-control" name="attributes[{{$key}}][content]" {{$type['required']}}></textarea>        @endif        <a>{{$helpText}}</a>    </div>    <div class="col-xs-12"><hr></div></div>